import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../css/ServiceCardDetails.css';
import { useParams } from 'react-router-dom';

const ServiceCardDetails = () => {
    const { id } = useParams();
    console.log(id);
    const [serviceDetails, setServiceDetails] = useState({});
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        axios.get(`http://localhost:4000/services/${id}`)
            .then((response) => {
                setServiceDetails(response.data[0]);
                setLoading(false);
            })
            .catch((error) => {
                console.error('Error fetching service details:', error);
            });
    }, [id]);

    if (loading) {
        return <p>Loading...</p>;
    }

    return (
        <div className="service-details-container">
            <img src={serviceDetails.ImageURL} alt={`${serviceDetails.FirstName} ${serviceDetails.LastName}`} />
            <h2>{serviceDetails.FirstName} {serviceDetails.LastName}</h2>
            <p><b>Service Type:</b> {serviceDetails.ServiceType}</p>
            <p><b>Ratings:</b> {serviceDetails.Ratings} <i className="fa fa-star ratings-yellow-star" aria-hidden="true"></i></p>
            <p><b>Address:</b> {serviceDetails.Address1}, {serviceDetails.City}, {serviceDetails.State} {serviceDetails.ZipCode}</p>
            <p><b>Discounted Price:</b> ${serviceDetails.DiscountedPrice}</p>
            <p><b>Description:</b> {serviceDetails.Description}</p>
        </div>
    );
}

export default ServiceCardDetails;
