import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import '../css/OrderHistory.css';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';

const OrderHistory = () => {
    const [data, setData] = useState([]);
    const { id } = useParams();
    const [selectedOrder, setSelectedOrder] = useState(null);

    useEffect(() => {
        axios.get(`http://localhost:4000/orderhistory/${id}`)
            .then(response => {
                setData(response.data);
            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }, [id]);

    const handleViewDetails = (order) => {
        setSelectedOrder(order);
    };

    const renderServiceDetails = (serviceNum, serviceType) => {
        if (selectedOrder[`SP${serviceNum}UserId`]) {
            return (
                <div className="service-details">
                    <h3>Service {serviceNum}: {serviceType}</h3>
                    <p>Name: {selectedOrder[`SP${serviceNum}FirstName`]} {selectedOrder[`SP${serviceNum}LastName`]}</p>
                    <p>Address: {selectedOrder[`SP${serviceNum}Address1`]}, {selectedOrder[`SP${serviceNum}Address2`]}, {selectedOrder[`SP${serviceNum}City`]}, New Jersey, {selectedOrder[`SP${serviceNum}Country`]} - {selectedOrder[`SP${serviceNum}ZipCode`]}</p>
                    <p>Email: {selectedOrder[`SP${serviceNum}EmailId`]}</p>
                    <p>Contact: {selectedOrder[`SP${serviceNum}ContactNo`]}</p>
                </div>
            );
        }
        return null;
    };

    return (
        <div>
            <Navbar />

            <div className="header-container">
                <h2>Order History</h2>
            </div>

            <div className="container">
                {data.map(order => (
                    <div key={order.BookingId} className="card">
                        <h3>Booking ID: {order.BookingId}</h3>
                        <p>Booking Start Date: {order.BookingStartDate}</p>
                        <p>Booking End Date: {order.BookingEndDate}</p>
                        <p>Net Amount: {order.NetAmount}</p>
                        <p>Tax: {order.Tax}</p>
                        <p>Discount: {order.Discount}</p>
                        <h4>Total Price: ${order.TotalPrice}</h4>
                        <button className="details-button" onClick={() => handleViewDetails(order)}>View Details</button>
                    </div>
                ))}

                {selectedOrder && (
                    <div className="details-panel">
                        <h2>Details for Booking ID: {selectedOrder.BookingId}</h2>
                        {renderServiceDetails(1, "Laundry")}
                        {renderServiceDetails(2, "House Cleaning")}
                        {renderServiceDetails(3, "PetCare")}
                        <button className="close-button" onClick={() => setSelectedOrder(null)}>Close</button>
                    </div>
                )}
            </div>

            <Footer />
        </div>
    );
}

export default OrderHistory;
