import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const OrderHistory = () => {
    const [data, setData] = useState([]);
    const { id } = useParams();
    useEffect(() => {
        axios.get(`http://localhost:4000/orderhistory/${id}`)
            .then(response => {
                setData(response.data);
            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }, [id]);

    const cardStyle = {
        border: 'none',
        boxShadow: '0px 0px 15px rgba(0, 0, 0, 0.1)',
        borderRadius: '10px',
        backgroundColor: '#fff',
        padding: '20px',
        marginBottom: '20px',
        color: '#672ab2',
    };

    return (
        <div style={{ margin: '20px', backgroundColor: '#f5f5f5', padding: '20px' }}>
            <h2 style={{ color: '#672ab2', borderBottom: '2px solid #672ab2', paddingBottom: '10px' }}>Order History</h2>
            {data.map(order => (
                <div key={order.BookingId} style={cardStyle}>
                    <h3>Booking ID: {order.BookingId}</h3>
                    <p>Booking Start Date: {order.BookingStartDate}</p>
                    <p>Booking End Date: {order.BookingEndDate}</p>
                    <p>Customer Name: {order.CustFirstName} {order.CustLastName}</p>
                    <h4>Total Price: ${order.TotalPrice}</h4>
                    <button style={{ backgroundColor: '#672ab2', color: 'white', border: 'none', padding: '10px 15px', borderRadius: '5px', cursor: 'pointer' }}>View Details</button>
                </div>
            ))}
        </div>
    );
}

export default OrderHistory;
